import{_ as a,c as t,o as r,a3 as i}from"./chunks/framework.Coj2bmTX.js";const h=JSON.parse('{"title":"瑞士 -> 瑞士 (Switzerland)","description":"","frontmatter":{},"headers":[],"relativePath":"瑞士/index.md","filePath":"docs/瑞士/index.md","lastUpdated":1718124115000}'),l={name:"瑞士/index.md"};function n(o,e,d,s,p,c){return r(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="瑞士-switzerland" tabindex="-1">瑞士 (Switzerland) <a class="header-anchor" href="#瑞士-switzerland" aria-label="Permalink to “瑞士 (Switzerland)”">​</a></h1><p><img src="https://i.imgur.com/HUm2oe3.jpg" alt="Switzerland photo by Huang An Sheng" loading="lazy"></p><p>瑞士聯邦為中歐國家，由 26 個州組成。瑞士是一個內陸國家，在地理上被劃分成阿爾卑斯山、瑞士高原及侏羅山脈三部份，其山地總面積達 41,285 平方公里，當中以阿爾卑斯山最為廣闊。瑞士高原不僅是瑞士 800 萬人口的集中區域，也是該國主要城市日內瓦及經濟中心蘇黎世的所在地。瑞士的自然風光及氣候條件十分優越，因而素有「世界公園」或「世界花園」之美譽。</p><ul><li>貨幣 瑞士法郎 (CHF)</li><li>時區 UTC+1(夏令時 UTC+2)</li><li>行駛方向 靠右行駛</li><li>官方語言 德語、法語、義大利語、羅曼什語</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2><ul><li><a href="https://zh.wikipedia.org/zh-tw/%E7%91%9E%E5%A3%AB" target="_blank" rel="noreferrer">Wikipedia 瑞士</a></li><li><a href="https://www.boca.gov.tw/sp-foof-countrycp-01-72-d01b5-1.html" target="_blank" rel="noreferrer">外交部領事事務所 瑞士</a></li></ul>',6)])])}const f=a(l,[["render",n]]);export{h as __pageData,f as default};
