import{_ as t,c as a,o as l,a3 as i}from"./chunks/framework.Coj2bmTX.js";const c=JSON.parse('{"title":"瑞士 -> 聖加侖州 -> Stiftsbibliothek St. Gallen","description":"","frontmatter":{},"headers":[],"relativePath":"瑞士/聖加侖州/Stiftsbibliothek St Gallen.md","filePath":"docs/瑞士/聖加侖州/Stiftsbibliothek St Gallen.md","lastUpdated":1737303891000}'),r={name:"瑞士/聖加侖州/Stiftsbibliothek St Gallen.md"};function s(n,e,o,b,f,h){return l(),a("div",null,[...e[0]||(e[0]=[i('<h1 id="stiftsbibliothek-st-gallen" tabindex="-1">Stiftsbibliothek St. Gallen <a class="header-anchor" href="#stiftsbibliothek-st-gallen" aria-label="Permalink to “Stiftsbibliothek St. Gallen”">​</a></h1><p>聖加侖修道院圖書館 (德語：Stiftsbibliothek，英語：Abbey library of Saint Gall)，名大廳上的天花鑲有描繪四場大公會議的濕壁畫，分別是第一次尼西亞公會議、第一次君士坦丁堡公會議、以弗所公會議及迦克墩公會議。圖書館是瑞士最古老的圖書館，因此收藏了大量珍貴的手抄本及搖籃本。1983 年，圖書館及修道院被指定為世界遺產。</p><p>館內有木馬屠城記的手繪記錄本以及一具木乃伊。</p><p>入館時必須將隨身行李背包寄放到寄物櫃內，且需要更換館內提供的室內拖鞋才可入場參觀。</p><ul><li>地址 Klosterhof 6D, 9000 St. Gallen, Switzerland</li><li>電話 +41712273416</li><li><a href="https://www.stiftsbezirk.ch/de/" target="_blank" rel="noreferrer">官方網站</a></li></ul><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2><iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2699.447457748949!2d9.373882276151186!3d47.422717700657735!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x479b1e4c45b2cf47%3A0x363e38457295682a!2sAbbey%20Library%20of%20Saint%20Gall!5e0!3m2!1sen!2stw!4v1690743133352!5m2!1sen!2stw" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2><ul><li><a href="https://www.stiftsbezirk.ch/de/" target="_blank" rel="noreferrer">Weltkulturerbe Stiftsbezirk</a></li></ul>',9)])])}const m=t(r,[["render",s]]);export{c as __pageData,m as default};
